        </view>
      </view>
      <view class="rule-pup">
        <ActivityPup 
          :show="rulePupOptions.show"
          :bg-color="'rgba(250, 247, 246, 1)'"
          :is-bottom="true"
          :custom-style="{bottom:'-100%',borderRadius: '32rpx 32rpx 0px 0px'}"
          :bg-style="{background:'rgba(0, 0, 0, 0.1)'}"
          @close="closePup"
        >
          <view>
            <view class="cny-pup-header">
              <image
                class="cny-pup-header-icon"
